function ejecutar() //Funcion que ejecuta todo.
{
  var peso = document.getElementById("peso").value;  //peso del usuario
  var planetSelected = document.getElementById("select").value;  //planeta seleccionado
  var content = document.getElementById("result"); //etiqueta p que da el resultado.
  var etiqueta = document.getElementById("changer");  //etiqueta image que da la imagen.
  var gravedad = {   //valores de gravedad de cada planeta
    JUPITER: 24.79,
    VENUS: 8.87,
    MERCURIO: 3.7,
    MARTE: 3.72,
    TIERRA: 9.81,
    SATURNO: 10.44,
    URANO: 8.87,
    NEPTUNO: 11.15
  }
  var write;
  var pesoFinal;

  switch (planetSelected) { //condicionales para cada planeta
    case "Jupiter":
      pesoFinal = (peso * gravedad.JUPITER / gravedad.TIERRA).toFixed(2);  //calculo para calcular tu peso, dependiendo el planeta.
      write = true;  //condicion para que se afirme que se va a ascribir lo obtenido en este case.
      break;
    case "Venus":
      pesoFinal = (peso * gravedad.VENUS / gravedad.TIERRA).toFixed(2);
      write = true
      break;
    case "Mercurio":
      pesoFinal = (peso * gravedad.MERCURIO / gravedad.TIERRA).toFixed(2);
      write = true;
      break;
    case "Marte":
      pesoFinal = (peso * gravedad.MARTE / gravedad.TIERRA).toFixed(2);
      write = true;
      break;
    case "Tierra":
      pesoFinal = peso;
      content.innerHTML = "";  //Como la tierra es especialita pues tiene que tener su prpio innerHTML porque se le agrega "la" antes de planeta.
      content.innerHTML = "<li>Tu peso en la <strong>" + planetSelected + "</strong> es de " + pesoFinal + " KG</li> <br>";  //que escribe el texto, solo que con la palabra "la tierra".
      write = false;
      etiqueta.src = "data:image/jpeg;base64," //src imagen tierra.
      break;
    case "Saturno":
      pesoFinal = (peso * gravedad.SATURNO / gravedad.TIERRA).toFixed(2);
      write = true;
      break;
    case "Urano":
      pesoFinal = (peso * gravedad.URANO / gravedad.TIERRA).toFixed(2);
      write = true;
      break;
    case "Neptuno":
      pesoFinal = (peso * gravedad.NEPTUNO / gravedad.TIERRA).toFixed(2);
      write = true;
      break;
  }

  if (write == true)
  {
    switch (planetSelected) { // para mostrar el texto de resultado de cada uno con sus respectiva imagen
      case "Mercurio":
        content.innerHTML = ""; //lo que se muestra en la etiqueta p.
        content.innerHTML = "<li>Tu peso en <strong>" + planetSelected + "</strong> es de " + pesoFinal + " KG</li> <br>" ;  //etiqueta "p" en la que se va a escribir todo.
        etiqueta.src = "data:image/jpeg;base64,"; //src imagen mercurio
        break;
      case "Marte":
        content.innerHTML = "";
        content.innerHTML = "<li>Tu peso en <strong>" + planetSelected + "</strong> es de " + pesoFinal + " KG</li> <br>" ;  //etiqueta "p" en la que se va a escribir todo.
        etiqueta.src = "data:image/jpeg;base64,"; //src imagen marte
        break;
      case "Venus":
        content.innerHTML = "";
        content.innerHTML = "<li>Tu peso en <strong>" + planetSelected + "</strong> es de " + pesoFinal + " KG</li> <br>" ;  //etiqueta "p" en la que se va a escribir todo.
        etiqueta.src = "data:image/jpeg;base64,"; //src imagen venus
        break;
      case "Jupiter":
        content.innerHTML = "";
        content.innerHTML = "<li>Tu peso en <strong>" + planetSelected + "</strong> es de " + pesoFinal + " KG</li> <br>" ;  //etiqueta "p" en la que se va a escribir todo.
        etiqueta.src = "data:image/jpeg;base64,"; //src imagen jupiter
        break;
      case "Saturno":
        content.innerHTML = "";
        content.innerHTML = "<li>Tu peso en <strong>" + planetSelected + "</strong> es de " + pesoFinal + " KG</li> <br>" ;  //etiqueta "p" en la que se va a escribir todo.
        etiqueta.src = "data:image/jpeg;base64,"; //src imagen saturno
        break;
      case "Urano":
        content.innerHTML = "";
        content.innerHTML = "<li>Tu peso en <strong>" + planetSelected + "</strong> es de " + pesoFinal + " KG</li> <br>" ;  //etiqueta "p" en la que se va a escribir todo.
        etiqueta.src = "data:image/jpeg;base64,"; //src imagen urano
        break;
      case "Neptuno":
        content.innerHTML = "";
        content.innerHTML = "<li>Tu peso en <strong>" + planetSelected + "</strong> es de " + pesoFinal + " KG</li> <br>" ;  //etiqueta "p" en la que se va a escribir todo.
        etiqueta.src = "data:image/jpeg;base64,"; //src imagen neptuno
        break;
    }
  }
}
